# python3 -m unittest test_main

class NeurtonStarTest(unittest.TestCase):
    name = "J0000+0000"
    sys = "base"
    chem = "s1"
    rel = False
    fc_key = 1 
    flux_key = 1
    w_key = "null"

    m_ns = 1.5
    r_ns = 12.0
    v_rot = 700.0
    i_ang = 60.0
    lum = 0.1

    NS = NeurtonStar(name=name, sys=sys, chem=chem, rel=rel,
                    fc_key=fc_key, flux_key=flux_key, w_key=w_key, 
                    m_ns=m_ns, r_ns=r_ns, v_rot=v_rot, i_ang=i_ang, lum=lum)
    
    NS.init_paramters()
        
    def test_NS_Parameters_1_key(self):
        self.assertEqual(self.name, self.NS.name)
        self.assertEqual(self.sys, self.NS.sys)
        self.assertEqual(self.chem, self.NS.chem)
        self.assertEqual(self.rel, self.NS.rel)
        self.assertEqual(self.fc_key, self.NS.fc_key)
        self.assertEqual(self.flux_key, self.NS.flux_key)
        self.assertEqual(self.w_key, self.NS.w_key)

    def test_NS_Parameters_2_initial(self):
        self.assertEqual(self.m_ns, self.NS.m_ns)
        self.assertEqual(self.r_ns, self.NS.r_ns)
        self.assertEqual(self.v_rot, self.NS.v_rot)
        self.assertEqual(self.i_ang, self.NS.i_ang)
        self.assertEqual(self.lum, self.NS.lum)

    def test_NS_Init_1_phisical(self):
        M_ns = 2.9865e33
        R_ns = 1.2e6
        R_sch = 443450.7844370508
        g = 1.742876032389798e14
        log_g = 14.24126649762995
        zst = 1.259424189272979
        surf_0 = 2.2840549754772964e12

        self.assertEqual(M_ns, self.NS.M)
        self.assertEqual(R_ns, self.NS.R)
        self.assertEqual(R_sch, self.NS.R_sch)
        self.assertEqual(g, self.NS.g)
        self.assertEqual(log_g, self.NS.log_g)
        self.assertEqual(zst, self.NS.zsch)
        self.assertEqual(surf_0, self.NS.surf_0)

    def test_NS_Init_2_chemical(self):
        X_hyd = 0.7374
        sigma_T = 0.34748
        w_b_0 = [0.27879591838812257, 0.3098462859708896, 0.31735305589148827, 0.364893953513819, 0.3553335847318445, 0.3736605843785679, 0.3520885020879058, 0.34802069280828957, 0.32741129089397014]
        T_c_0 = [1.105026, 1.17652, 1.27281, 1.342584, 1.4680199999999999, 1.574829, 1.7359680000000002, 1.8911419999999999, 2.090707]

        self.assertEqual(X_hyd, self.NS.X_hyd)
        self.assertEqual(sigma_T, self.NS.sigma_T)

        for k in range(0, 6):
            with self.subTest(i=k):
                self.assertEqual(w_b_0[k], self.NS.w_b[0][k])

        for k in range(0, 6):
            with self.subTest(i=k):
                self.assertEqual(T_c_0[k], self.NS.T_c[0][k])

    def test_NS_Init_3_photometrical(self):
        Flux_edd = 1.5036870120300983e25
        T_edd = 1.8018216527889073e7
        Theta_edd = 2.4874147916750865e-9
        Epsilon_edd = 1.5525218447618998
        Lum_edd = 2.7210079124882748e38
        Lum_obs = 1.7154803347779853e38

