import unittest

from ns_atm.classes import *

# python3 -m unittest test_main

name = "J0000+0000"
sys = "base"
chem = "s1"
rel = True
fc_key = 1 
flux_key = 1
w_key = "null"

m_ns = 1.519
r_ns = 15.48
v_rot = 700.0
Arr_i_ang = [0.0, 45.0, 90.0]
Arr_lum = [0.1, 0.9]
n_phi = 120
n_theta = 60
n_nu = 1000

Arr_file = ["sp_01", "sp90"]

class SpectraDependingAngle(unittest.TestCase):
    def test_lum_01_i_00(self):
        i_ang = self.Arr_i_ang[0]
        lum = self.Arr_lum[0]
        file = self.Arr_file[0]

        NS = NeurtonStar(name=self.name, sys=self.sys, chem=self.chem, rel=self.rel,
                        fc_key=self.fc_key, flux_key=self.flux_key, w_key=self.w_key, 
                        m_ns=self.m_ns, r_ns=self.r_ns, v_rot=self.v_rot, 
                        i_ang=i_ang, lum=lum)
     
        NS.init_paramters()
        NS.init_grid(n_phi=self.n_phi, n_theta=self.n_theta, n_nu=self.n_nu)
        NS.integrate()
        Surf = NS.Surface
        
        with open(f'ns_atm/spectra/{file}.dat', 'r') as f:
            pass

        self.assertEqual(rnd(1), rnd(1))


if __name__ == "__main__":
    unittest.main()




